import{_ as i,c as h,o as a,a3 as t}from"./chunks/framework.vep7XCK_.js";const p=JSON.parse('{"title":"👥 1.2 Đối tượng tham gia","description":"","frontmatter":{},"headers":[],"relativePath":"chuong-1/doi-tuong.md","filePath":"chuong-1/doi-tuong.md","lastUpdated":1756802256000}'),o={name:"chuong-1/doi-tuong.md"};function l(c,n,g,u,e,s){return a(),h("div",null,[...n[0]||(n[0]=[t(`<h1 id="👥-1-2-đoi-tuong-tham-gia" tabindex="-1">👥 1.2 Đối tượng tham gia <a class="header-anchor" href="#👥-1-2-đoi-tuong-tham-gia" aria-label="Permalink to &quot;👥 1.2 Đối tượng tham gia&quot;">​</a></h1><h2 id="🎯-gioi-thieu-lien-he-thuc-te" tabindex="-1">🎯 Giới thiệu &amp; Liên hệ thực tế <a class="header-anchor" href="#🎯-gioi-thieu-lien-he-thuc-te" aria-label="Permalink to &quot;🎯 Giới thiệu &amp; Liên hệ thực tế&quot;">​</a></h2><p>Bạn có bao giờ tự hỏi: &quot;Mình có phù hợp với khóa học này không?&quot; hay &quot;Liệu mình có đủ kiến thức nền tảng để học về AI?&quot;</p><p>Đây là những câu hỏi rất phổ biến khi bắt đầu học một kỹ năng mới. Hôm nay chúng ta sẽ tìm hiểu xem bạn có phù hợp với khóa học này không và cần chuẩn bị gì để học hiệu quả nhất.</p><p><strong>Câu hỏi chính</strong>: Ai có thể tham gia khóa học này và cần chuẩn bị gì?</p><h2 id="🔧-đoi-tuong-phu-hop-voi-khoa-hoc" tabindex="-1">🔧 Đối tượng phù hợp với khóa học <a class="header-anchor" href="#🔧-đoi-tuong-phu-hop-voi-khoa-hoc" aria-label="Permalink to &quot;🔧 Đối tượng phù hợp với khóa học&quot;">​</a></h2><h3 id="🎓-nhom-chinh-nguoi-lam-trong-giao-duc" tabindex="-1">🎓 Nhóm chính: Người làm trong giáo dục <a class="header-anchor" href="#🎓-nhom-chinh-nguoi-lam-trong-giao-duc" aria-label="Permalink to &quot;🎓 Nhóm chính: Người làm trong giáo dục&quot;">​</a></h3><h4 id="👨‍🏫-giao-vien-pho-thong-mam-non-→-thpt" tabindex="-1">👨‍🏫 Giáo viên phổ thông (Mầm non → THPT) <a class="header-anchor" href="#👨‍🏫-giao-vien-pho-thong-mam-non-→-thpt" aria-label="Permalink to &quot;👨‍🏫 Giáo viên phổ thông (Mầm non → THPT)&quot;">​</a></h4><p><strong>Phù hợp vì:</strong></p><ul><li>Có thể áp dụng ngay vào việc soạn giáo án hàng ngày</li><li>Cần tạo nhiều tài liệu: giáo án, slide, đề kiểm tra</li><li>Phải đối mặt với học sinh có mức độ khác nhau trong cùng lớp</li></ul><p><strong>Ví dụ cụ thể:</strong></p><ul><li>Cô Hoa - Giáo viên Toán THCS: Dùng AI tạo bài tập từ cơ bản đến nâng cao</li><li>Thầy Nam - Giáo viên Văn THPT: Dùng AI phân tích tác phẩm và tạo câu hỏi thảo luận</li><li>Cô Lan - Giáo viên Mầm non: Dùng AI tạo truyện tranh giáo dục</li></ul><h4 id="👩‍🎓-giang-vien-đai-hoc-cao-đang" tabindex="-1">👩‍🎓 Giảng viên đại học/cao đẳng <a class="header-anchor" href="#👩‍🎓-giang-vien-đai-hoc-cao-đang" aria-label="Permalink to &quot;👩‍🎓 Giảng viên đại học/cao đẳng&quot;">​</a></h4><p><strong>Phù hợp vì:</strong></p><ul><li>Cần nghiên cứu và cập nhật kiến thức liên tục</li><li>Có nhiều sinh viên trong lớp, cần cá nhân hóa</li><li>Thường xuyên làm nghiên cứu và viết báo cáo</li></ul><p><strong>Ví dụ cụ thể:</strong></p><ul><li>TS. Minh - Giảng viên Kinh tế: Dùng AI phân tích xu hướng thị trường cho bài giảng</li><li>ThS. Hà - Giảng viên Ngoại ngữ: Dùng AI tạo đối화 và bài tập nghe nói</li></ul><h4 id="🏢-nguoi-đao-tao-trong-doanh-nghiep" tabindex="-1">🏢 Người đào tạo trong doanh nghiệp <a class="header-anchor" href="#🏢-nguoi-đao-tao-trong-doanh-nghiep" aria-label="Permalink to &quot;🏢 Người đào tạo trong doanh nghiệp&quot;">​</a></h4><p><strong>Phù hợp vì:</strong></p><ul><li>Cần cập nhật nội dung đào tạo nhanh chóng</li><li>Đào tạo nhân viên ở nhiều cấp độ khác nhau</li><li>Phải đo lường hiệu quả đào tạo</li></ul><h3 id="🎯-nhom-mo-rong-nguoi-ho-tro-giao-duc" tabindex="-1">🎯 Nhóm mở rộng: Người hỗ trợ giáo dục <a class="header-anchor" href="#🎯-nhom-mo-rong-nguoi-ho-tro-giao-duc" aria-label="Permalink to &quot;🎯 Nhóm mở rộng: Người hỗ trợ giáo dục&quot;">​</a></h3><h4 id="👨‍👩‍👧‍👦-phu-huynh-co-con-đang-hoc" tabindex="-1">👨‍👩‍👧‍👦 Phụ huynh có con đang học <a class="header-anchor" href="#👨‍👩‍👧‍👦-phu-huynh-co-con-đang-hoc" aria-label="Permalink to &quot;👨‍👩‍👧‍👦 Phụ huynh có con đang học&quot;">​</a></h4><p><strong>Tại sao nên học:</strong></p><ul><li>Hiểu được cách con em đang sử dụng AI</li><li>Hỗ trợ con học bài hiệu quả hơn</li><li>Tạo tài liệu ôn tập phù hợp với con</li></ul><p><strong>Ví dụ thực tế:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Bà Linh (có con lớp 5): </span></span>
<span class="line"><span>- Dùng AI tạo bài tập Toán phù hợp với mức độ của con</span></span>
<span class="line"><span>- Tạo truyện kể có tính giáo dục</span></span>
<span class="line"><span>- Giải thích các khái niệm khó bằng cách đơn giản</span></span></code></pre></div><h4 id="🎓-hoc-sinh-sinh-vien" tabindex="-1">🎓 Học sinh, sinh viên <a class="header-anchor" href="#🎓-hoc-sinh-sinh-vien" aria-label="Permalink to &quot;🎓 Học sinh, sinh viên&quot;">​</a></h4><p><strong>Tại sao nên học:</strong></p><ul><li>Sử dụng AI hỗ trợ học tập một cách đúng đắn</li><li>Tạo tài liệu ôn tập cá nhân</li><li>Phát triển kỹ năng tự học</li></ul><p><strong>Lưu ý đặc biệt:</strong></p><ul><li>Học cách dùng AI như một &quot;người thầy ảo&quot;</li><li>Tránh lạm dụng AI để làm bài tập thay mình</li><li>Hiểu được giới hạn và cách kiểm chứng thông tin</li></ul><h4 id="📚-quan-ly-giao-duc" tabindex="-1">📚 Quản lý giáo dục <a class="header-anchor" href="#📚-quan-ly-giao-duc" aria-label="Permalink to &quot;📚 Quản lý giáo dục&quot;">​</a></h4><p><strong>Tại sao nên học:</strong></p><ul><li>Hiểu xu hướng ứng dụng AI trong giáo dục</li><li>Đưa ra quyết định đầu tư công nghệ phù hợp</li><li>Hỗ trợ đội ngũ giáo viên áp dụng AI</li></ul><h2 id="🔬-phan-tich-muc-đo-phu-hop" tabindex="-1">🔬 Phân tích mức độ phù hợp <a class="header-anchor" href="#🔬-phan-tich-muc-đo-phu-hop" aria-label="Permalink to &quot;🔬 Phân tích mức độ phù hợp&quot;">​</a></h2><h3 id="⭐-rat-phu-hop-90-100" tabindex="-1">⭐ Rất phù hợp (90-100%) <a class="header-anchor" href="#⭐-rat-phu-hop-90-100" aria-label="Permalink to &quot;⭐ Rất phù hợp (90-100%)&quot;">​</a></h3><p><strong>Đặc điểm:</strong></p><ul><li>Làm việc trực tiếp với việc giảng dạy/đào tạo</li><li>Có ít nhất 6 tháng kinh nghiệm giảng dạy</li><li>Sẵn sàng thử nghiệm công cụ mới</li><li>Có máy tính và kết nối internet ổn định</li></ul><p><strong>Nhóm người:</strong></p><ul><li>Giáo viên các cấp có từ 6 tháng kinh nghiệm trở lên</li><li>Giảng viên đại học/cao đẳng</li><li>Người đào tạo trong doanh nghiệp</li></ul><h3 id="⭐-kha-phu-hop-70-89" tabindex="-1">⭐ Khá phù hợp (70-89%) <a class="header-anchor" href="#⭐-kha-phu-hop-70-89" aria-label="Permalink to &quot;⭐ Khá phù hợp (70-89%)&quot;">​</a></h3><p><strong>Đặc điểm:</strong></p><ul><li>Thỉnh thoảng có liên quan đến giảng dạy</li><li>Muốn hỗ trợ người khác học tập</li><li>Có kiến thức cơ bản về máy tính</li></ul><p><strong>Nhóm người:</strong></p><ul><li>Phụ huynh quan tâm đến việc học của con</li><li>Học sinh/sinh viên năm cuối</li><li>Trợ giảng, gia sư</li></ul><h3 id="⭐-co-the-tham-gia-50-69" tabindex="-1">⭐ Có thể tham gia (50-69%) <a class="header-anchor" href="#⭐-co-the-tham-gia-50-69" aria-label="Permalink to &quot;⭐ Có thể tham gia (50-69%)&quot;">​</a></h3><p><strong>Đặc điểm:</strong></p><ul><li>Quan tâm đến AI và giáo dục</li><li>Chưa có kinh nghiệm giảng dạy trực tiếp</li><li>Muốn tìm hiểu về ứng dụng AI</li></ul><p><strong>Nhóm người:</strong></p><ul><li>Sinh viên các ngành sư phạm</li><li>Người làm công tác hỗ trợ giáo dục</li><li>Người muốn chuyển sang lĩnh vực giáo dục</li></ul><h2 id="💻-kien-thuc-va-ky-nang-can-co" tabindex="-1">💻 Kiến thức và kỹ năng cần có <a class="header-anchor" href="#💻-kien-thuc-va-ky-nang-can-co" aria-label="Permalink to &quot;💻 Kiến thức và kỹ năng cần có&quot;">​</a></h2><h3 id="✅-bat-buoc-minimum-requirements" tabindex="-1">✅ Bắt buộc (Minimum Requirements) <a class="header-anchor" href="#✅-bat-buoc-minimum-requirements" aria-label="Permalink to &quot;✅ Bắt buộc (Minimum Requirements)&quot;">​</a></h3><h4 id="💻-ky-nang-may-tinh-co-ban" tabindex="-1">💻 Kỹ năng máy tính cơ bản <a class="header-anchor" href="#💻-ky-nang-may-tinh-co-ban" aria-label="Permalink to &quot;💻 Kỹ năng máy tính cơ bản&quot;">​</a></h4><ul><li><strong>Biết duyệt web</strong>: Mở trình duyệt, truy cập website</li><li><strong>Biết đăng ký tài khoản</strong>: Tạo account Google, email</li><li><strong>Biết download file</strong>: Tải về tài liệu, hình ảnh</li><li><strong>Biết copy/paste</strong>: Sao chép và dán văn bản</li></ul><p><strong>Test nhanh</strong>: Bạn có thể tự mình đăng ký tài khoản Gmail không?</p><ul><li>✅ Có → Đủ điều kiện</li><li>❌ Không → Cần học thêm kỹ năng máy tính cơ bản</li></ul><h4 id="🌐-co-ket-noi-internet" tabindex="-1">🌐 Có kết nối internet <a class="header-anchor" href="#🌐-co-ket-noi-internet" aria-label="Permalink to &quot;🌐 Có kết nối internet&quot;">​</a></h4><ul><li>Tốc độ tối thiểu: 5 Mbps</li><li>Ổn định trong 2-3 giờ liên tục</li><li>Có thể truy cập các trang web quốc tế</li></ul><h4 id="📱-thiet-bi-phu-hop" tabindex="-1">📱 Thiết bị phù hợp <a class="header-anchor" href="#📱-thiet-bi-phu-hop" aria-label="Permalink to &quot;📱 Thiết bị phù hợp&quot;">​</a></h4><p><strong>Tối thiểu:</strong></p><ul><li>Smartphone + WiFi</li><li>Có thể xem video và đọc tài liệu</li></ul><p><strong>Khuyến khích:</strong></p><ul><li>Laptop hoặc máy tính bàn</li><li>Màn hình từ 13 inch trở lên</li><li>Bàn phím vật lý để gõ nhanh</li></ul><h3 id="🌟-khong-bat-buoc-nhung-co-thi-tot" tabindex="-1">🌟 Không bắt buộc nhưng có thì tốt <a class="header-anchor" href="#🌟-khong-bat-buoc-nhung-co-thi-tot" aria-label="Permalink to &quot;🌟 Không bắt buộc nhưng có thì tốt&quot;">​</a></h3><h4 id="📚-kinh-nghiem-giang-day" tabindex="-1">📚 Kinh nghiệm giảng dạy <a class="header-anchor" href="#📚-kinh-nghiem-giang-day" aria-label="Permalink to &quot;📚 Kinh nghiệm giảng dạy&quot;">​</a></h4><ul><li>Biết quy trình soạn giáo án cơ bản</li><li>Hiểu tâm lý học sinh ở độ tuổi mình dạy</li><li>Có kinh nghiệm sử dụng PowerPoint</li></ul><h4 id="🔧-ky-nang-cong-nghe" tabindex="-1">🔧 Kỹ năng công nghệ <a class="header-anchor" href="#🔧-ky-nang-cong-nghe" aria-label="Permalink to &quot;🔧 Kỹ năng công nghệ&quot;">​</a></h4><ul><li>Biết sử dụng Microsoft Office (Word, Excel, PowerPoint)</li><li>Đã từng dùng Google Drive, Dropbox</li><li>Biết cài đặt app trên điện thoại</li></ul><h4 id="🧠-tu-duy-mo" tabindex="-1">🧠 Tư duy mở <a class="header-anchor" href="#🧠-tu-duy-mo" aria-label="Permalink to &quot;🧠 Tư duy mở&quot;">​</a></h4><ul><li>Sẵn sàng thử nghiệm công cụ mới</li><li>Không sợ &quot;làm hỏng&quot; gì khi thử nghiệm</li><li>Kiên nhẫn khi học kỹ năng mới</li></ul><h2 id="🏋️-thuc-hanh-tu-đanh-gia-muc-đo-phu-hop" tabindex="-1">🏋️ Thực hành: Tự đánh giá mức độ phù hợp <a class="header-anchor" href="#🏋️-thuc-hanh-tu-đanh-gia-muc-đo-phu-hop" aria-label="Permalink to &quot;🏋️ Thực hành: Tự đánh giá mức độ phù hợp&quot;">​</a></h2><h3 id="bai-tap-1-checklist-tu-đanh-gia-5-phut" tabindex="-1">Bài tập 1: Checklist tự đánh giá (5 phút) <a class="header-anchor" href="#bai-tap-1-checklist-tu-đanh-gia-5-phut" aria-label="Permalink to &quot;Bài tập 1: Checklist tự đánh giá (5 phút)&quot;">​</a></h3><p>Hãy đánh dấu ✅ vào những mục bạn đã có:</p><p><strong>👨‍🏫 Về công việc/mục tiêu:</strong></p><ul><li>[ ] Tôi đang làm việc trong lĩnh vực giáo dục</li><li>[ ] Tôi thường xuyên phải chuẩn bị tài liệu giảng dạy</li><li>[ ] Tôi muốn tiết kiệm thời gian trong công việc</li><li>[ ] Tôi muốn nâng cao chất lượng bài giảng</li></ul><p><strong>💻 Về kỹ năng công nghệ:</strong></p><ul><li>[ ] Tôi có thể truy cập internet và duyệt web</li><li>[ ] Tôi có tài khoản Gmail hoặc có thể tạo mới</li><li>[ ] Tôi biết copy/paste văn bản</li><li>[ ] Tôi có thể download và mở file</li></ul><p><strong>🎯 Về động lực:</strong></p><ul><li>[ ] Tôi sẵn sàng dành 1-2 giờ/ngày để học</li><li>[ ] Tôi không ngại thử nghiệm công cụ mới</li><li>[ ] Tôi muốn áp dụng ngay những gì học được</li><li>[ ] Tôi có kiên nhẫn khi gặp khó khăn</li></ul><p><strong>📊 Kết quả:</strong></p><ul><li>10-12 ✅: Rất phù hợp, bắt đầu ngay!</li><li>7-9 ✅: Khá phù hợp, cần chuẩn bị thêm một chút</li><li>4-6 ✅: Có thể học được, nhưng cần thời gian</li><li>Dưới 4 ✅: Nên học thêm kỹ năng cơ bản trước</li></ul><h3 id="bai-tap-2-xac-đinh-lo-trinh-hoc-10-phut" tabindex="-1">Bài tập 2: Xác định lộ trình học (10 phút) <a class="header-anchor" href="#bai-tap-2-xac-đinh-lo-trinh-hoc-10-phut" aria-label="Permalink to &quot;Bài tập 2: Xác định lộ trình học (10 phút)&quot;">​</a></h3><p>Dựa trên kết quả tự đánh giá, chọn lộ trình phù hợp:</p><h4 id="🚀-lo-trinh-express-đa-co-10-✅" tabindex="-1">🚀 Lộ trình Express (Đã có 10+ ✅) <a class="header-anchor" href="#🚀-lo-trinh-express-đa-co-10-✅" aria-label="Permalink to &quot;🚀 Lộ trình Express (Đã có 10+ ✅)&quot;">​</a></h4><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Tuần 1: Chương 1-2 (Nền tảng)</span></span>
<span class="line"><span>Tuần 2: Chương 3-4 (Công cụ cơ bản)  </span></span>
<span class="line"><span>Tuần 3: Chương 5-6 (Ứng dụng nâng cao)</span></span>
<span class="line"><span>Tuần 4: Chương 7-9 (Thực hành &amp; hoàn thiện)</span></span></code></pre></div><h4 id="🎯-lo-trinh-standard-co-7-9-✅" tabindex="-1">🎯 Lộ trình Standard (Có 7-9 ✅) <a class="header-anchor" href="#🎯-lo-trinh-standard-co-7-9-✅" aria-label="Permalink to &quot;🎯 Lộ trình Standard (Có 7-9 ✅)&quot;">​</a></h4><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Tuần 1: Chương 1 + ôn luyện kỹ năng máy tính</span></span>
<span class="line"><span>Tuần 2: Chương 2-3  </span></span>
<span class="line"><span>Tuần 3: Chương 4-5</span></span>
<span class="line"><span>Tuần 4: Chương 6-7</span></span>
<span class="line"><span>Tuần 5: Chương 8-9 + thực hành thêm</span></span></code></pre></div><h4 id="🐌-lo-trinh-slow-steady-co-4-6-✅" tabindex="-1">🐌 Lộ trình Slow &amp; Steady (Có 4-6 ✅) <a class="header-anchor" href="#🐌-lo-trinh-slow-steady-co-4-6-✅" aria-label="Permalink to &quot;🐌 Lộ trình Slow &amp; Steady (Có 4-6 ✅)&quot;">​</a></h4><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Tuần 1-2: Chương 1 + học kỹ năng máy tính cơ bản</span></span>
<span class="line"><span>Tuần 3-4: Chương 2-3</span></span>
<span class="line"><span>Tuần 5-6: Chương 4-5  </span></span>
<span class="line"><span>Tuần 7-8: Chương 6-7</span></span>
<span class="line"><span>Tuần 9-10: Chương 8-9 + ôn tập tổng thể</span></span></code></pre></div><h3 id="bai-tap-3-chuan-bi-tai-khoan-15-phut" tabindex="-1">Bài tập 3: Chuẩn bị tài khoản (15 phút) <a class="header-anchor" href="#bai-tap-3-chuan-bi-tai-khoan-15-phut" aria-label="Permalink to &quot;Bài tập 3: Chuẩn bị tài khoản (15 phút)&quot;">​</a></h3><p>Hãy chuẩn bị các tài khoản sau để thực hành:</p><ol><li><p><strong>Tài khoản Google</strong> (gmail.com)</p><ul><li>Để truy cập ChatGPT, Canva, và các công cụ khác</li><li>Cách làm: Truy cập gmail.com → &quot;Tạo tài khoản&quot;</li></ul></li><li><p><strong>Tài khoản ChatGPT</strong> (chat.openai.com)</p><ul><li>Công cụ AI quan trọng nhất trong khóa học</li><li>Cách làm: Truy cập chat.openai.com → &quot;Sign up&quot; → Dùng email Google</li></ul></li><li><p><strong>Tài khoản Canva</strong> (canva.com)</p><ul><li>Để tạo hình ảnh và slide</li><li>Cách làm: Truy cập canva.com → &quot;Đăng ký&quot; → Dùng email Google</li></ul></li></ol><p><strong>Lưu ý</strong>: Tất cả đều có phiên bản miễn phí, không cần thanh toán!</p><h2 id="📋-tom-tat-chuan-bi-cho-bai-sau" tabindex="-1">📋 Tóm tắt &amp; Chuẩn bị cho bài sau <a class="header-anchor" href="#📋-tom-tat-chuan-bi-cho-bai-sau" aria-label="Permalink to &quot;📋 Tóm tắt &amp; Chuẩn bị cho bài sau&quot;">​</a></h2><h3 id="🔑-điem-quan-trong-can-nho" tabindex="-1">🔑 Điểm quan trọng cần nhớ <a class="header-anchor" href="#🔑-điem-quan-trong-can-nho" aria-label="Permalink to &quot;🔑 Điểm quan trọng cần nhớ&quot;">​</a></h3><ol><li><strong>Khóa học dành cho mọi người</strong> làm việc với giáo dục, từ giáo viên đến phụ huynh</li><li><strong>Không cần kiến thức AI trước đó</strong>, chỉ cần kỹ năng máy tính cơ bản</li><li><strong>Chọn lộ trình phù hợp</strong> với trình độ và thời gian của bạn</li><li><strong>Chuẩn bị tài khoản trước</strong> để thực hành ngay</li></ol><h3 id="📚-bai-sau-chung-ta-se-hoc-gi" tabindex="-1">📚 Bài sau chúng ta sẽ học gì? <a class="header-anchor" href="#📚-bai-sau-chung-ta-se-hoc-gi" aria-label="Permalink to &quot;📚 Bài sau chúng ta sẽ học gì?&quot;">​</a></h3><p>Trong <a href="/ai-teaching/chuong-1/loi-ich.html">Bài 1.3: Lợi ích khi ứng dụng AI</a>, chúng ta sẽ khám phá:</p><ul><li>Những lợi ích cụ thể AI mang lại cho giảng dạy</li><li>So sánh &quot;trước&quot; và &quot;sau&quot; khi dùng AI</li><li>Các nghiên cứu thực tế về hiệu quả của AI trong giáo dục</li><li>Câu chuyện thành công từ các giáo viên đã áp dụng AI</li></ul><h3 id="🎯-chuan-bi-cho-bai-sau" tabindex="-1">🎯 Chuẩn bị cho bài sau <a class="header-anchor" href="#🎯-chuan-bi-cho-bai-sau" aria-label="Permalink to &quot;🎯 Chuẩn bị cho bài sau&quot;">​</a></h3><ul><li>Hoàn thành việc tạo tài khoản ChatGPT và Canva</li><li>Thử đăng nhập và làm quen với giao diện</li><li>Suy nghĩ về những khó khăn hiện tại trong công việc giảng dạy của bạn</li></ul><hr><div class="tip custom-block"><p class="custom-block-title">💡 Mẹo chuẩn bị</p><p>Hãy tạo một thư mục riêng trên máy tính để lưu tất cả tài liệu từ khóa học này. Đặt tên như &quot;AI Teaching Course&quot; để dễ tìm kiếm!</p></div><div class="warning custom-block"><p class="custom-block-title">⚠️ Quan trọng về bảo mật</p><p>Khi tạo tài khoản, hãy sử dụng mật khẩu mạnh và không chia sẻ thông tin đăng nhập với người khác. Đặc biệt cẩn thận với các email lừa đảo giả mạo!</p></div>`,104)])])}const d=i(o,[["render",l]]);export{p as __pageData,d as default};
