import{_ as n,c as i,o as h,a3 as c}from"./chunks/framework.vep7XCK_.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{"layout":"home","hero":{"name":"Ứng dụng AI vào Giảng dạy","text":"Khóa học toàn diện 🎓🤖","tagline":"Học cách sử dụng AI để nâng cao hiệu quả giảng dạy - Hoàn toàn bằng tiếng Việt","image":{"src":"/logo.png","alt":"AI Teaching Course"},"actions":[{"theme":"brand","text":"Bắt đầu học ngay","link":"/chuong-1/muc-tieu"},{"theme":"alt","text":"Học bằng video","link":"https://haizuka.vn/course/?c=ai-teaching"}]},"features":[{"icon":"🎯","title":"Từ cơ bản đến nâng cao","details":"Bắt đầu từ con số 0, không cần kiến thức trước về AI"},{"icon":"🆓","title":"Công cụ miễn phí","details":"Chỉ sử dụng các công cụ AI miễn phí như ChatGPT Free, Canva AI"},{"icon":"🇻🇳","title":"Hoàn toàn tiếng Việt","details":"Nội dung được viết đặc biệt cho người Việt, dễ hiểu"},{"icon":"💼","title":"Thực hành ngay","details":"Áp dụng trực tiếp vào công việc giảng dạy hàng ngày"},{"icon":"🏫","title":"Cho mọi cấp học","details":"Phù hợp với giáo viên từ tiểu học đến đại học"},{"icon":"⚡","title":"Tiết kiệm thời gian","details":"Tự động hóa việc soạn giáo án, tạo câu hỏi, chấm bài"}]},"headers":[],"relativePath":"index.md","filePath":"index.md","lastUpdated":1756802256000}'),o={name:"index.md"};function a(g,t,r,d,l,e){return h(),i("div",null,[...t[0]||(t[0]=[c('<h2 id="🌟-chao-mung-đen-voi-khoa-hoc-ai-trong-giang-day" tabindex="-1">🌟 Chào mừng đến với khóa học AI trong giảng dạy! <a class="header-anchor" href="#🌟-chao-mung-đen-voi-khoa-hoc-ai-trong-giang-day" aria-label="Permalink to &quot;🌟 Chào mừng đến với khóa học AI trong giảng dạy!&quot;">​</a></h2><p>Khóa học này được thiết kế đặc biệt cho các <strong>giáo viên, giảng viên và những người làm trong lĩnh vực giáo dục</strong> muốn tận dụng sức mạnh của AI để nâng cao hiệu quả công việc.</p><h3 id="✨-tai-sao-khoa-hoc-nay-đac-biet" tabindex="-1">✨ Tại sao khóa học này đặc biệt? <a class="header-anchor" href="#✨-tai-sao-khoa-hoc-nay-đac-biet" aria-label="Permalink to &quot;✨ Tại sao khóa học này đặc biệt?&quot;">​</a></h3><ul><li><strong>🎯 Thiết thực</strong>: Tập trung vào việc giải quyết những vấn đề thực tế trong giảng dạy</li><li><strong>💰 Tiết kiệm</strong>: Chỉ sử dụng công cụ miễn phí, không tốn kém</li><li><strong>📚 Dễ hiểu</strong>: Ngôn ngữ đơn giản, ví dụ cụ thể từ thực tế giảng dạy</li><li><strong>⚡ Hiệu quả ngay</strong>: Có thể áp dụng ngay sau mỗi bài học</li></ul><h3 id="🚀-ban-se-hoc-đuoc-gi" tabindex="-1">🚀 Bạn sẽ học được gì? <a class="header-anchor" href="#🚀-ban-se-hoc-đuoc-gi" aria-label="Permalink to &quot;🚀 Bạn sẽ học được gì?&quot;">​</a></h3><ol><li><strong>Hiểu về AI</strong>: Từ khái niệm cơ bản đến ứng dụng trong giáo dục</li><li><strong>Sử dụng công cụ</strong>: ChatGPT, Canva AI, Microsoft Office AI và nhiều hơn nữa</li><li><strong>Soạn giảng thông minh</strong>: Tạo giáo án, slide, câu hỏi tự động</li><li><strong>Quản lý lớp học</strong>: Chatbot hỗ trợ, chấm bài tự động, theo dõi tiến độ</li><li><strong>Thực hành thực tế</strong>: Case study và bài tập từ tình huống thật</li></ol><h3 id="🎯-khoa-hoc-danh-cho-ai" tabindex="-1">🎯 Khóa học dành cho ai? <a class="header-anchor" href="#🎯-khoa-hoc-danh-cho-ai" aria-label="Permalink to &quot;🎯 Khóa học dành cho ai?&quot;">​</a></h3><ul><li><strong>Giáo viên</strong> các cấp học từ tiểu học đến đại học</li><li><strong>Giảng viên</strong> cao đẳng, đại học</li><li><strong>Người đào tạo</strong> trong doanh nghiệp</li><li><strong>Học sinh, sinh viên</strong> muốn hỗ trợ học tập</li><li><strong>Phụ huynh</strong> quan tâm đến việc hỗ trợ con em</li></ul><h3 id="📖-cau-truc-khoa-hoc" tabindex="-1">📖 Cấu trúc khóa học <a class="header-anchor" href="#📖-cau-truc-khoa-hoc" aria-label="Permalink to &quot;📖 Cấu trúc khóa học&quot;">​</a></h3><p>Khóa học gồm <strong>9 chương</strong> với <strong>26 bài học</strong> chi tiết:</p><table tabindex="0"><thead><tr><th>Chương</th><th>Nội dung</th><th>Thời gian</th></tr></thead><tbody><tr><td>1</td><td>Giới thiệu khóa học</td><td>1-2 giờ</td></tr><tr><td>2</td><td>Kiến thức nền tảng về AI</td><td>2-3 giờ</td></tr><tr><td>3</td><td>Công cụ AI hỗ trợ giảng dạy</td><td>3-4 giờ</td></tr><tr><td>4</td><td>Ứng dụng AI trong soạn giảng</td><td>3-4 giờ</td></tr><tr><td>5</td><td>AI trong Microsoft Office</td><td>2-3 giờ</td></tr><tr><td>6</td><td>AI trong quản lý lớp học</td><td>2-3 giờ</td></tr><tr><td>7</td><td>Thực hành &amp; Case Study</td><td>3-4 giờ</td></tr><tr><td>8</td><td>Đạo đức khi dùng AI</td><td>1-2 giờ</td></tr><tr><td>9</td><td>Tổng kết &amp; Định hướng</td><td>1-2 giờ</td></tr></tbody></table><h3 id="🎬-phuong-phap-hoc" tabindex="-1">🎬 Phương pháp học <a class="header-anchor" href="#🎬-phuong-phap-hoc" aria-label="Permalink to &quot;🎬 Phương pháp học&quot;">​</a></h3><p>Mỗi bài học được thiết kế theo cấu trúc:</p><ul><li><strong>🎯 Giới thiệu &amp; Liên hệ thực tế</strong>: Tình huống cụ thể</li><li><strong>🔧 Khái niệm &amp; Cú pháp</strong>: Hướng dẫn từng bước</li><li><strong>🔬 Phân tích &amp; Giải thích</strong>: Tại sao và khi nào dùng</li><li><strong>💻 Ví dụ minh họa</strong>: Demo thực tế với công cụ</li><li><strong>🏋️ Thực hành</strong>: Bài tập từ cơ bản đến nâng cao</li><li><strong>📋 Tóm tắt</strong>: Ôn tập và chuẩn bị bài sau</li></ul><hr><div class="tip custom-block"><p class="custom-block-title">💡 Bắt đầu ngay hôm nay!</p><p>Hãy bắt đầu với <a href="/ai-teaching/chuong-1/muc-tieu.html">Chương 1: Mục tiêu học tập</a> để khám phá những điều tuyệt vời mà AI có thể mang lại cho việc giảng dạy của bạn!</p></div><div class="warning custom-block"><p class="custom-block-title">⚠️ Chuẩn bị trước khi học</p><ul><li>Tài khoản Google (để truy cập ChatGPT và các công cụ khác)</li><li>Kết nối internet ổn định</li><li>Máy tính hoặc tablet để thực hành</li><li>Tinh thần học hỏi và thử nghiệm!</li></ul></div>',17)])])}const m=n(o,[["render",a]]);export{u as __pageData,m as default};
